mapper.select:
and at.type_no LIKE CONCAT(#{typeNo},'%')
and (a.article_title like CONCAT('%',#{keyword},'%') or a.article_tag like CONCAT('%',#{keyword},'%'))
order by sort_num DESC,article_id DESC limit #{start},#{size}



	is_online=1


			and at.type_no like CONCAT(#{type},'%')
			and a.article_type=#{type}


	and a.is_top=#{top}
	and a.is_command=#{command}

		and (a.article_title like CONCAT('%',#{keyword},'%') or a.article_tag like CONCAT('%',#{keyword},'%'))


order by sort_num DESC,article_id DESC
limit #{start},#{size}

select count(article_id) from tbl_article_infor a inner join sys_user u on a.modify_user=u.user_id inner join tbl_article_type at on a.article_type=at.type_id LEFT join tbl_article_type pt on at.type_parent=pt.type_id
is_online=1
and at.type_no like CONCAT(#{type},'%') and a.article_type=#{type}
and a.is_top=#{top} and a.is_command=#{command}
and (a.article_title like CONCAT('%',#{keyword},'%') or a.article_tag like CONCAT('%',#{keyword},'%'))

mapper.update:
update tbl_article_infor
sort_num=#{sortNum}, article_title=#{articleTitle}, article_img=#{articleImg}, article_tag=#{articleTag}, article_desc=#{articleDesc}, article_detail=#{articleDetail}, read_num=#{readNum}, article_type=#{articleType.typeId}, is_top=#{isTop}, is_command=#{isCommand}, is_online=#{isOnline}, is_en=#{isEn}, send_user=#{sendUser} , send_date=#{sendDate}, modify_user=#{modifyUser.userId} , modify_time=#{modifyTime},
where article_id=#{articleId}

